import { HeartTwoTone, HeartOutlined } from '@ant-design/icons';
import { Button, List, Skeleton, Tooltip } from 'antd';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Select } from 'antd';

const { Option } = Select;

const SnippetsList = () => {

    const [initLoading, setInitLoading] = useState(true);
    const [loading, setLoading] = useState(false);
    const [list, setList] = useState([]);
    const [tags, setTags] = useState([]);
    const [count, setCount] = useState(0);
    const [currentTags, setCurrentTags] = useState([])

    const getSnippets = (tags = [], loadMore = false) => {
        axios.get(`http://localhost:3000/getSnippets`, {
            params: {
                tags: tags,
                count: count
            }
        })
            .then((res) => {
                console.log(res)
                setInitLoading(false);
                setLoading(false);
                setList([...list, ...res.data.results]);
            });
    }

    const getTags = () => {
        axios.get(`http://localhost:3000/getTags`)
            .then((res) => {
                setTags(res.data.results);
            });
    }

    useEffect(() => {

        getSnippets(currentTags);
        getTags()

    }, [count]);

    const onLoadMore = () => {
        setLoading(true);
        setCount(count + 1);
    };

    const markAsFav = () => {

        // call api to mark as favourite

    }

    const handleTags = (value) => {
        setLoading(true);
        setCount(0);
        setCurrentTags(value);
        setList([])        
    }

    const loadMore =
        !initLoading && !loading ? (
            <div
                style={{
                    textAlign: 'center',
                    marginTop: 12,
                    height: 32,
                    lineHeight: '32px',
                }}
            >
                <Button onClick={() => onLoadMore()}>loading more</Button>
            </div>
        ) : null;
    return (
        <>
            <Select
                mode="tags"
                style={{
                    width: '80%',
                }}
                onChange={handleTags}
                tokenSeparators={[',']}
            >
                {
                    tags.map((tag) => {
                        return <Option key={tag.tag}>{tag.tag}</Option>
                    })
                }
            </Select>
            <List
                className="demo-loadmore-list"
                style={{
                    padding: 20,
                    width: "50%",
                    textAlign: 'justify',
                    marginLeft: "25%"
                }}
                loading={initLoading}
                itemLayout="horizontal"
                loadMore={loadMore}
                dataSource={list}
                renderItem={(item) => (
                    <List.Item
                        actions={[<Tooltip title="Mark as favourite">
                            <Button
                                shape='circle'
                                icon={item.fav ? <HeartTwoTone twoToneColor='red' /> : <HeartOutlined />}
                                onClick={markAsFav}
                            />
                        </Tooltip>]}
                    >
                        <Skeleton avatar title={false} loading={item.loading} active>
                            <List.Item.Meta
                                // title={<a href="https://ant.design">{item.name?.last}</a>}
                                description={<pre>{item.snippet}</pre>}
                            />
                        </Skeleton>
                    </List.Item>
                )}
            />
        </>
    );
};

export default SnippetsList;
