import { HeartTwoTone, HeartOutlined } from '@ant-design/icons';
import { Button, List, Skeleton, Tooltip } from 'antd';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Select } from 'antd';

const { Option } = Select;
const children = [];
const count = 3;

const SnippetsList = () => {

    const [initLoading, setInitLoading] = useState(true);
    const [loading, setLoading] = useState(false);
    const [data, setData] = useState([]);
    const [list, setList] = useState([]);
    const [tags, setTags] = useState([]);

    getSnippets = () => {
        axios.get(`http://localhost:3000/getSnippets`, {
            params: {
                tags: tags
            }
        })
            .then((res) => {
                console.log(res)
                setInitLoading(false);
                setData(res.data.results);
                setList(res.data.results);
            });
    }

    getTags = () => {
        axios.get(`http://localhost:3000/getTags`)
            .then((res) => {
                setTags(res.data.results);
            });
    }

    useEffect(() => {

        getSnippets()


        getTags()


    }, []);

    const onLoadMore = () => {
        setLoading(true);
        setList(
            data.concat(
                [...new Array(count)].map(() => ({
                    loading: true,
                    name: {},
                    picture: {},
                })),
            ),
        );
        // fetch(fakeDataUrl)
        //     .then((res) => res.json())
        //     .then((res) => {
        //         const newData = data.concat(res.results);
        //         setData(newData);
        //         setList(newData);
        //         setLoading(false); // Resetting window's offsetTop so as to display react-virtualized demo underfloor.
        //         // In real scene, you can using public method of react-virtualized:
        //         // https://stackoverflow.com/questions/46700726/how-to-use-public-method-updateposition-of-react-virtualized

        //         window.dispatchEvent(new Event('resize'));
        //     });
    };

    const markAsFav = () => {

        // call api to mark as favourite

    }

    const handleTags = (value) => {
        console.log(value)
    }

    const loadMore =
        !initLoading && !loading ? (
            <div
                style={{
                    textAlign: 'center',
                    marginTop: 12,
                    height: 32,
                    lineHeight: '32px',
                }}
            >
                <Button onClick={() => onLoadMore()}>loading more</Button>
            </div>
        ) : null;
    return (
        <>
            <Select
                mode="tags"
                style={{
                    width: '80%',
                }}
                onChange={handleTags}
                tokenSeparators={[',']}
            >
                {
                    tags.map((tag) => {
                        return <Option key={tag.id}>{tag.tag}</Option>
                    })
                }
            </Select>
            <List
                className="demo-loadmore-list"
                style={{
                    padding: 20,
                    width: "50%",
                    textAlign: 'justify',
                    marginLeft: "25%"
                }}
                loading={initLoading}
                itemLayout="horizontal"
                loadMore={loadMore}
                dataSource={list}
                renderItem={(item) => (
                    <List.Item
                        actions={[<Tooltip title="Mark as favourite">
                            <Button
                                shape='circle'
                                icon={item.fav ? <HeartTwoTone twoToneColor='red' /> : <HeartOutlined />}
                                onClick={markAsFav}
                            />
                        </Tooltip>]}
                    >
                        <Skeleton avatar title={false} loading={item.loading} active>
                            <List.Item.Meta
                                // title={<a href="https://ant.design">{item.name?.last}</a>}
                                description={<pre>{item.snippet}</pre>}
                            />
                        </Skeleton>
                    </List.Item>
                )}
            />
        </>
    );
};

export default SnippetsList;
